// frontend/src/components/PortStatusModal.js
// This component displays a modal showing the status (connected/available)
// of all ports for a given Patch Panel or Server.
// It now includes collapse/expand functionality for the port list and a print button.

import React, { useState } from "react";
import {
  XCircle,
  CheckCircle,
  CircleDot,
  WifiOff,
  Printer,
  ChevronDown,
  ChevronUp,
} from "lucide-react"; // Added Printer, ChevronDown, ChevronUp icons

function PortStatusModal({ isOpen, onClose, data, entityType }) {
  const [isPortListExpanded, setIsPortListExpanded] = useState(true); // New state for list expansion

  if (!isOpen || !data) return null;

  const entityName = data.patch_panel_name || data.server_name || "N/A";
  const totalPorts = data.total_ports || 0;
  const ports = data.ports || [];

  const togglePortList = () => {
    setIsPortListExpanded(!isPortListExpanded);
  };

  const handlePrint = () => {
    // Uses the browser's native print functionality.
    // Users can choose "Save as PDF" from their browser's print dialog.
    window.print();
  };

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex justify-center items-center z-50 p-4">
      <div className="bg-white rounded-lg shadow-xl w-full max-w-2xl max-h-[90vh] overflow-y-auto transform transition-all duration-300 scale-100 opacity-100">
        <div className="p-6 print-container">
          {" "}
          {/* Added print-container class for print styling */}
          {/* Modal Header */}
          <div className="flex justify-between items-center pb-3 border-b border-gray-200 mb-4 no-print">
            {" "}
            {/* no-print class to hide buttons in print view */}
            <h2 className="text-2xl font-bold text-gray-800">
              {entityType === "patch_panels" ? "Patch Panel" : "Server"} Port
              Status: {entityName}
            </h2>
            <div className="flex space-x-2">
              <button
                onClick={handlePrint}
                className="text-gray-500 hover:text-blue-700 p-1 rounded-full hover:bg-blue-100 transition-colors duration-200"
                title="Print Report"
              >
                <Printer size={24} />
              </button>
              <button
                onClick={onClose}
                className="text-gray-500 hover:text-gray-700 p-1 rounded-full hover:bg-gray-100 transition-colors duration-200"
                title="Close"
              >
                <XCircle size={24} />
              </button>
            </div>
          </div>
          {/* Header for Print (visible only in print) */}
          <h2 className="text-2xl font-bold text-gray-800 mb-4 print-only">
            {entityType === "patch_panels" ? "Patch Panel" : "Server"} Port
            Status Report: {entityName}
          </h2>
          {/* Summary */}
          <div className="mb-6 text-center text-lg text-gray-700">
            <p>
              Total Ports:{" "}
              <span className="font-semibold text-blue-600">{totalPorts}</span>
            </p>
            <p>
              Connected Ports:{" "}
              <span className="font-semibold text-green-600">
                {ports.filter((p) => p.is_connected).length}
              </span>
            </p>
            <p>
              Available Ports:{" "}
              <span className="font-semibold text-gray-500">
                {ports.filter((p) => !p.is_connected).length}
              </span>
            </p>
          </div>
          {/* Collapse/Expand Button for Port List */}
          <div
            className="flex justify-between items-center mb-4 cursor-pointer no-print"
            onClick={togglePortList}
          >
            <h3 className="text-xl font-semibold text-gray-700">
              Port Details
            </h3>
            <button
              className="p-1 rounded-full bg-gray-100 hover:bg-gray-200 transition-colors duration-200"
              title={isPortListExpanded ? "Collapse" : "Expand"}
            >
              {isPortListExpanded ? (
                <ChevronUp size={20} />
              ) : (
                <ChevronDown size={20} />
              )}
            </button>
          </div>
          {/* Port Grid (Conditional Rendering based on isPortListExpanded) */}
          {isPortListExpanded && (
            <div className="grid grid-cols-4 sm:grid-cols-6 md:grid-cols-8 lg:grid-cols-10 gap-3 animate-fade-in-down">
              {ports.map((port) => (
                <div
                  key={port.port_number}
                  className={`p-2 rounded-md flex flex-col items-center justify-center text-center text-xs font-medium border
                            ${
                              port.is_connected
                                ? port.is_up
                                  ? "bg-green-100 border-green-300 text-green-800"
                                  : "bg-red-100 border-red-300 text-red-800"
                                : "bg-gray-100 border-gray-300 text-gray-600"
                            }`}
                >
                  <span className="text-sm font-bold">
                    Port {port.port_number}
                  </span>
                  {port.is_connected ? (
                    <>
                      {port.is_up ? (
                        <CheckCircle
                          size={18}
                          className="text-green-500 mt-1"
                          title="Connected & Up"
                        />
                      ) : (
                        <WifiOff
                          size={18}
                          className="text-red-500 mt-1"
                          title="Connected & Down"
                        />
                      )}
                      <span
                        className="mt-1 truncate max-w-full"
                        title={port.connected_by_pc}
                      >
                        {port.connected_by_pc}
                      </span>
                    </>
                  ) : (
                    <CircleDot
                      size={18}
                      className="text-gray-500 mt-1"
                      title="Available"
                    />
                  )}
                </div>
              ))}
            </div>
          )}
          {/* Detailed List of Connected/Available Ports for Print (Always visible in print) */}
          <div className="mt-6 print-only">
            {" "}
            {/* print-only class to show only in print view */}
            <h3 className="text-xl font-semibold text-gray-700 mb-3">
              Detailed Port List:
            </h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
              <div>
                <h4 className="font-bold text-green-700 mb-2">
                  Connected Ports:
                </h4>
                {ports.filter((p) => p.is_connected).length > 0 ? (
                  <ul className="list-disc list-inside space-y-1">
                    {ports
                      .filter((p) => p.is_connected)
                      .map((port) => (
                        <li key={`print-conn-${port.port_number}`}>
                          Port {port.port_number}: Connected by{" "}
                          {port.connected_by_pc} (Status:{" "}
                          {port.is_up ? "Up" : "Down"})
                        </li>
                      ))}
                  </ul>
                ) : (
                  <p>No connected ports.</p>
                )}
              </div>
              <div>
                <h4 className="font-bold text-gray-700 mb-2">
                  Available Ports:
                </h4>
                {ports.filter((p) => !p.is_connected).length > 0 ? (
                  <ul className="list-disc list-inside space-y-1">
                    {ports
                      .filter((p) => !p.is_connected)
                      .map((port) => (
                        <li key={`print-avail-${port.port_number}`}>
                          Port {port.port_number}
                        </li>
                      ))}
                  </ul>
                ) : (
                  <p>No available ports.</p>
                )}
              </div>
            </div>
          </div>
          {/* Legend */}
          <div className="mt-6 pt-4 border-t border-gray-200 flex flex-wrap justify-center gap-4 text-sm text-gray-700 no-print">
            <div className="flex items-center">
              <CheckCircle size={18} className="text-green-500 mr-2" />{" "}
              Connected & Up
            </div>
            <div className="flex items-center">
              <WifiOff size={18} className="text-red-500 mr-2" /> Connected &
              Down
            </div>
            <div className="flex items-center">
              <CircleDot size={18} className="text-gray-500 mr-2" /> Available
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default PortStatusModal;
